import java.util.*;

public class CourseSuggestionOption 
{
	
	private Course course;
	
	public CourseSuggestionOption(Course course)
	{
		this.setCourse(course);
	}
	
	public Course getCourse()
	{
		return this.course;
	}
	
	public void setCourse(Course course)
	{
		this.course = course;
	}

	@Override
	public String toString() {
		// TODO Auto-generated method stub
		return this.getCourse().getGeneralCourseName() + " (" + this.getCourse().getCourseTerm() + ")";
	}
}
	
